        /// <summary>
        /// Image URI to Base64 String
        /// </summary>
        /// <param name="uri"></param>
        /// <returns></returns>
        public static async Task<string> UriImgToString(string uri)
        {
            HttpClient httpClient = new HttpClient();

            HttpResponseMessage httpResponse = await httpClient.GetAsync(uri);

            // Get Image By UrI and Convert Base64 Bytes
            byte[] iamgeBase64 = await httpResponse.Content.ReadAsByteArrayAsync();

            // byte[] to string
            string strIamgeBase64 = System.Convert.ToBase64String(iamgeBase64);
            return strIamgeBase64;
        }

        /// <summary>
        /// Output Image Base64
        /// </summary>
        /// <param name="uri"></param>
        /// <returns></returns>
        public static async Task<ImageSource> ImgBase64Source(string uri)
        {
            HttpClient httpClient = new HttpClient();

            HttpResponseMessage httpResponse = await httpClient.GetAsync(App.URL + uri);

            // Get Image By UrI and Convert Base64 Bytes
            byte[] iamgeBase64 = await httpResponse.Content.ReadAsByteArrayAsync();

            // File Save
            // File.WriteAllBytes(System.Environment.GetFolderPath(System.Environment.SpecialFolder.MyPictures), urlBytes);

            // byte[] to string
            // string strIamgeBase64 = Convert.ToBase64String(iamgeBase64);

            // string Base64 inctnce as ImageSource
            // Avatar = ImageSource.FromStream(() => new MemoryStream(iamgeBase64));

            return ImageSource.FromStream(() => new MemoryStream(iamgeBase64));
        }

