        /// <summary>
        /// URI to ImageSource
        /// </summary>
        /// <param name="uri"></param>
        /// <returns></returns>
        public static async Task<ImageSource> UriImgToImageSource(string uri)
        {
            string strBase64Img = await UriImgToString(uri);
            return Base64ToSource(strBase64Img);
        }

        /// <summary>
        /// String Base64 to ImageSource
        /// </summary>
        /// <param name="strBase64"></param>
        /// <returns></returns>
        public static ImageSource Base64ToSource(string strBase64)
        {
            byte[] byteBase64 = System.Convert.FromBase64String(strBase64);
            return ImageSource.FromStream(() => new MemoryStream(byteBase64));
        }

    }
